import axios from 'axios';

// Set up the base URL for your API
const API_BASE_URL = 'http://localhost:3001';

// Fetch transactions based on month, search text, and page
export const fetchTransactions = async (month, searchText, page) => {
  const response = await axios.get(`${API_BASE_URL}/api/transactions`, {
    params: { month, search: searchText, page },
  });
  return response.data;
};

// Fetch statistics for a selected month
export const fetchStatistics = async (month) => {
  const response = await axios.get(`${API_BASE_URL}/api/statistics`, {
    params: { month },
  });
  return response.data;
};

// Fetch data for the bar chart based on selected month
export const fetchBarChartData = async (month) => {
  const response = await axios.get(`${API_BASE_URL}/api/bar-chart`, {
    params: { month },
  });
  return response.data;
};

// Fetch data for the pie chart based on selected month
export const fetchPieChartData = async (month) => {
  const response = await axios.get(`${API_BASE_URL}/api/pie-chart`, {
    params: { month },
  });
  return response.data;
};
