import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api';

// Example function for fetching transactions
export const fetchTransactions = async (month, search = '', page = 1, perPage = 10) => {
  try {
    const response = await axios.get(`${API_BASE_URL}/transactions`, {
      params: { month, search, page, perPage }
    });
    return response.data;
  } catch (error) {
    console.error('Error fetching transactions:', error);
    throw error;
  }
};

export const fetchStatistics = async (month) => {
  try {
    const response = await axios.get(`${API_BASE_URL}/transactions/statistics`, {
      params: { month }
    });
    return response.data;
  } catch (error) {
    console.error('Error fetching statistics:', error);
    throw error;
  }
};

export const fetchBarChartData = async (month) => {
  try {
    const response = await axios.get(`${API_BASE_URL}/transactions/bar-chart`, {
      params: { month }
    });
    return response.data;
  } catch (error) {
    console.error('Error fetching bar chart data:', error);
    throw error;
  }
};

export const fetchPieChartData = async (month) => {
  try {
    const response = await axios.get(`${API_BASE_URL}/transactions/pie-chart`, {
      params: { month }
    });
    return response.data;
  } catch (error) {
    console.error('Error fetching pie chart data:', error);
    throw error;
  }
};
